mapper.select:
select count(*) from ding_dan dd left join ding_dan_zhuang_tai ddzt on dd.ddztId=ddzt.id left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where 1=1
and dd.ddh like CONCAT('%',#{ddh},'%')
and dd.ddztId=#{ddztId}

select dd.*,ddzt.mc ddztMc,yss.mc yssMc,fhdw.mc fhdwMc,shbm.mc shbmMc from ding_dan dd left join ding_dan_zhuang_tai ddzt on dd.ddztId=ddzt.id left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where 1=1
and dd.ddh like CONCAT('%',#{ddh},'%')
and dd.ddztId=#{ddztId}
order by dd.bjsj desc limit #{rowNum},#{rows}

select dd.*,wzlx.mc wzlxMc,wz.mc wzMc,yss.mc yssMc,fhdw.mc fhdwMc,shbm.mc shbmMc from ding_dan dd left join wu_zi_lei_xing wzlx on dd.wzlxId=wzlx.id left join wu_zi wz on wzlx.id=wz.wzlxId
 left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id
 left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where dd.id=#{id}
